svg.script:
edges = [[0,2],[2,3],[3,1],[1,0],[4,5],[5,7],[7,6],[6,4],[1,5],[4,0],[3,7],[2,6]]
faces = [[0,2,3,1],[4,5,7,6],[0,1,5,4],[1,3,7,5],[3,2,6,7],[2,0,4,6]]
depth = [0,0,0,0,0,0]

x_coordinates = [0.0,100.0,0.0,100.0,0.0,100.0,0.0,100.0];
y_coordinates = [0.0,0.0,100.0,100.0,0.0,0.0,100.0,100.0];
z_coordinates = [0.0,0.0,0.0,0.0,100.0,100.0,100.0,100.0];

centre_x = 50.0;
centre_y = 50.0;
centre_z = -0.0;

function init(evt)
{
	if(window.svgDocument == null)
	{
		svgDocument = evt.target.ownerDocument;
	}
	draw_object();
}

function draw_object()
{
	for(var i = 0; i < faces.length; i++)
	{
		face = svgDocument.getElementById('face-'+i);
		var d = 'M' + x_coordinates[faces[i][0]] + ' ' + y_coordinates[faces[i][0]];
		for(var j = 1; j < faces[i].length; j++)
		{
			d += ' ' + 'L' + x_coordinates[faces[i][j]] + ' ' + y_coordinates[faces[i][j]];
		}
		d += ' Z';
		face.setAttributeNS(null, 'd', d);
	}
}
